import React from 'react';
import { SpinnerContainer, SpinnerOverlay, LogoContainer } from './with-spinner.styles';
import { ReactComponent as Logo } from '../../assets/crown.svg';
import './spinner.styles.scss'
const WithSpinner = WrappedComponent => ({ isLoading, ...otherProps }) => {
    return isLoading ? (
        <SpinnerOverlay>
            <LogoContainer>
            {/* <Logo className='logo-spinner'/> */}

            <SpinnerContainer>
                <Logo className='logo-spinner3'/>
                </SpinnerContainer>
                {/* <Logo className='logo-spinner2'/> */}

            </LogoContainer>
            <h5 className='spinner-title'>I'll have you there shortly</h5>
        </SpinnerOverlay>
    ) : (
        <WrappedComponent {...otherProps} />
    )
}

export default WithSpinner;
