import { takeLatest, put, all, call } from "redux-saga/effects";
import {
  auth,
  googleProvider,
  createUserProfileDocument,

} from "../../firebase/firebase.utils";
import UserActionTypes from "./user.types";
import {
  googleSigninSuccess,
  googleSigninFailure,
  emailSigninSuccess,
  emailSigninFailure,
  signupFailure
} from './user.actions';






  export function* getSnapshotFromUserAuth(userAuth, additionalData) {
    try {
      const userRef = yield call(
        createUserProfileDocument,
        userAuth,
        additionalData
      );
      const userSnapshot = yield userRef.get();
      yield put(googleSigninSuccess({ id: userSnapshot.id, ...userSnapshot.data() }));
    } catch (error) {
      yield put(googleSigninFailure(error));
    }
  };
export function* signupWithEmailandPwd({payload: { email, password, displayName}}){
  try {
    const { user } = yield auth.createUserWithEmailAndPassword(
      email,
      password
    );
    yield put(signupSuccess({ user, additionalData: { displayName } }));
    }
   catch (error) {
    yield put(signupFailure(error))
  }
};
export function* signInAfterSignUp({ payload: { user, additionalData } }) {
  yield getSnapshotFromUserAuth(user, additionalData);
}



export function* signinWithGoogle() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider);
    yield getSnapshotFromUserAuth(user);
  } catch (error) {
    yield put(googleSigninFailure(error));
  }
};

export function* signinWithEmailandPwd({payload: { email, password}}) {
  try {
    const { user } = yield auth.signInWithEmailAndPassword(email, password);
    const userRef = yield call(createUserProfileDocument, user);
    const userSnapshot = yield userRef.get();
    console.log(user)
    yield put(
      emailSigninSuccess({ id: userSnapshot.id, ...userSnapshot.data()})
    )

  }catch(error){
    put(emailSigninFailure(error))
  }
};


export function* onGoogleSignInStart() {
  yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, signinWithGoogle);
};
export function* onEmailSigninStart() {
  yield takeLatest(UserActionTypes.EMAIL_SIGN_IN_START, signinWithEmailandPwd)
};
export function* onSignupStart() {
  yield takeLatest(UserActionTypes.SIGN_UP_START, signupWithEmailandPwd);
};

export function* userSagas() {
  yield all([
    call(onGoogleSignInStart),
    call(onEmailSigninStart)
  ])
}
