            required
          />            <label>login</label>

            <FormInput
            name='adminPassword'
            type='password'
            value={this.state.adminPassword}
            handleChange={this.handleChange}
            required
          />
           <label>password</label>
        </div>
         )  
     }
 }

 const mapStateToProps = createStructuredSelector({
    currentUser: selectCurrentUser
  })
