import styled from 'styled-components';

export const SpinnerOverlay = styled.div`
  height: 60vh;
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const SpinnerContainer = styled.div`
  display: inline-block;
  width: 17.567vh;
  height: 13.567vh;
  border: .2rem solid rgba(195, 195, 195, 0.6);
  border-radius: 50%;
  border-top-color: #636767;
  animation: spin .77667s ease-in-out infinite;
  -webkit-animation: spin .77667s ease-in-out infinite;
  @keyframes spin {
    to {
      -webkit-transform: rotate(277.333deg);
    }
  }
  @-webkit-keyframes spin {
    to {
      -webkit-transform: rotate(-355.333deg);
    }
  }
`;
