import React from "react";
import StripeCheckout from 'react-stripe-checkout';
import axios from 'axios';

const StripeCheckoutButton = ({ price }) => {
    const priceForStripe = price * 100;
    const publishable_key = 'pk_test_51JrB82LXLc6RQBpHwh9xIxjIZEPR9vvVOlT1rcOjDXAS2gubDCmvAPsd81LoJhScB1MjcE8yxsqfbo6jX5VI8Hz300SVfgd3pf';

    const onToken = token => {
        axios({
            url: 'letstalktostripe',
            method: 'post',
            data: {
                amount: priceForStripe,
                token
            }
        }).then(response => {
            alert('pament success', response)
        }).catch(error => {
            console.log('***-- PAYMENT ERROR --***  ');
            alert('there was an issue with your payment')
        })
    }
    const onOpened = () => {
        console.log('stripe payment window opened')
    }
    const onClosed = () => {
        console.log('stripe payment window closed')
    }
    return (
        <div className="total">
            <StripeCheckout
                label='Pay Now'
                name='CRWN Clothing Ltd.'
                billingAddress
                shippingAddress
                image='https://svgshare.com/i/CUz.svg'
                description={`Your total is $${price}`}
                amount={priceForStripe}
                panelLabel='Pay Now'
                token={onToken}
                stripeKey={publishable_key}
            />
        </div>
    )
}

export default StripeCheckoutButton;
