import styled from 'styled-components';

export const SpinnerOverlay = styled.div`
  height: 60vh;
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const SpinnerContainer = styled.div`
display:table-cell;
vertical-align:middle;width:100px;height:100px;"
width: 100px;
height: 70px;
background-color: transparent;

margin: 100px auto;
-webkit-animation: sk-rotateplane 1.2s infinite ease-in-out;
animation: sk-rotateplane 1.2s infinite ease-in-out;
}
}

@-webkit-keyframes sk-rotateplane {
0% { -webkit-transform: perspective(0px) }
50% { -webkit-transform: perspective(0px) rotateY(180deg) }
100% { -webkit-transform: perspective(0px) rotateY(180deg)  rotateX(180deg) }
}

@keyframes sk-rotateplane {
0% { 
  transform: perspective(0px) rotateX(0deg) rotateY(0deg);
  -webkit-transform: perspective(0px) rotateX(0deg) rotateY(0deg) 
} 50% { 
  transform: perspective(0px) rotateX(-280.1deg) rotateY(0deg);
  -webkit-transform: perspective(0px) rotateX(-80.1deg) rotateY(0deg) 
} 100% { 
  transform: perspective(0px) rotateX(-40deg) rotateY(-299.9deg);
  -webkit-transform: perspective(0px) rotateX(-280deg) rotateY(-179.9deg);
}
`;
